const colorPicker = document.getElementById('color-picker');
const colorBox = document.getElementById('color-box');
const rgbBox = document.getElementById('rgb-box');
const hexBox = document.getElementById('hex-box');

function updateBoxes(color) {
    colorBox.style.backgroundColor = color;
    const rgb = color.match(/\d+/g);
    rgbBox.textContent = `RGB: ${rgb[0]}, ${rgb[1]}, ${rgb[2]}`;
    const hex = '#' + rgb.map(c => (+c).toString(16).padStart(2, '0')).join('');
    hexBox.textContent = `HEX: ${hex}`;
}

updateBoxes(colorPicker.value);

colorPicker.addEventListener('change', function(event) {
    updateBoxes(event.target.value);
});

