const searchForm = document.getElementById('search-form');
const searchInput = document.getElementById('search-input');
const imageGrid = document.getElementById('image-grid');

async function searchImages(query) {
    const response = await fetch(`https://api.unsplash.com/search/photos?query=${encodeURIComponent(query)}&client_id=YOUR_ACCESS_KEY_HERE`);
    const data = await response.json();
    return data.results;
}

function displayImages(images) {
    let html = '';
    for (const image of images) {
        html += `<div class="image-container"><img src="${image.urls.regular}" alt="${image.alt_description}"></div>`;
    }
    imageGrid.innerHTML = html;
}

searchForm.addEventListener('submit', async function(event) {
    event.preventDefault();
    const query = searchInput.value.trim();
    if (query === '') {
        alert('Please enter a search term.');
        return;
    }
    const images = await searchImages(query);
    displayImages(images);
    searchInput.value = '';
});

