const noteForm = document.getElementById('note-form');
const noteTextarea = document.getElementById('note-text');
const noteList = document.getElementById('note-list');

function saveNote() {
    const note = noteTextarea.value.trim();
    if (note === '') {
        alert('Please enter a note.');
        return;
    }
    const notes = JSON.parse(localStorage.getItem('notes') || '[]');
    notes.push(note);
    localStorage.setItem('notes', JSON.stringify(notes));
    displayNotes();
    noteTextarea.value = '';
}

function displayNotes() {
    const notes = JSON.parse(localStorage.getItem('notes') || '[]');
    let html = '';
    for (const note of notes) {
        html += `<li>${note}</li>`;
    }
    noteList.innerHTML = html;
}

noteForm.addEventListener('submit', function(event) {
    event.preventDefault();
    saveNote();
});

displayNotes();

const apiKey = 'YOUR_API_KEY_HERE';

const searchForm = document.getElementById('search-form');
const searchInput = document.getElementById('search-input');
const imageGrid = document.getElementById('image-grid');

function searchImages(event) {
    event.preventDefault();
    const searchTerm = searchInput.value.trim();
    if (searchTerm === '') {
        alert('Please enter a search term.');
        return;
    }
    fetch(`https://api.flickr.com/services/rest/?method=flickr.photos.search&api_key=${apiKey}&text=${searchTerm}&format=json&nojsoncallback=1`)
        .then(response => response.json())
        .then(data => {
            const photos = data.photos.photo;
            let html = '';
            for (const photo of photos) {
                const imageUrl = `https://live.staticflickr.com/${photo.server}/${photo.id}_${photo.secret}_q.jpg`;
                html += `<img src="${imageUrl}" alt="${photo.title}">`;
            }
            imageGrid.innerHTML = html;
        })
        .catch(error => {
            console.error(error);
            alert('Error searching for images.');
        });
}

searchForm.addEventListener('submit', searchImages);

document.addEventListener('DOMContentLoaded', function() {
    const uploadForm = document.getElementById('upload-form');
    const fileList = document.getElementById('file-list');

    function uploadFile(event) {
        event.preventDefault();
        const formData = new FormData(uploadForm);
        const xhr = new XMLHttpRequest();
        xhr.open('POST', 'upload.php');
        xhr.onload = function() {
            if (xhr.status === 200) {
                addFileToList(xhr.responseText);
            } else {
                alert('Error uploading file.');
            }
        };
        xhr.send(formData);
    }

    function addFileToList(filename) {
        const li = document.createElement('li');
        const link = document.createElement('a');
        link.textContent = filename;
        link.href = `uploads/${filename}`;
        link.target = '_blank';
        const downloadBtn = document.createElement('button');
        downloadBtn.textContent = 'Download';
        downloadBtn.addEventListener('click', function() {
            window.open(`uploads/${filename}`, '_blank');
        });
        li.appendChild(link);
        li.appendChild(downloadBtn);
        fileList.appendChild(li);
    }

    uploadForm.addEventListener('submit', uploadFile);
});

document.addEventListener('DOMContentLoaded', function() {
    const addBtn = document.getElementById('add-btn');
    const newTaskInput = document.getElementById('new-task');
    const taskList = document.getElementById('task-list');

    function addTask() {
        const taskText = newTaskInput.value.trim();
        if (taskText === '') {
            alert('Please enter a task.');
            return;
        }
        const taskItem = document.createElement('li');
        const taskLabel = document.createElement('label');
        const taskCheckbox = document.createElement('input');
        taskCheckbox.type = 'checkbox';
        taskLabel.textContent = taskText;
        taskItem.appendChild(taskCheckbox);
        taskItem.appendChild(taskLabel);
        taskList.appendChild(taskItem);
        newTaskInput.value = '';
    }

    addBtn.addEventListener('click', addTask);
    newTaskInput.addEventListener('keyup', function(event) {
        if (event.key === 'Enter') {
            addTask();
        }
    });
});

