from flask import Flask, render_template
import datetime
import time

app = Flask(__name__)

@app.route('/')
def index():
    return render_template('index.html')

@app.route('/time')
def get_time():
    while True:
        time.sleep(1)
        now = datetime.datetime.now()
        yield 'data: %s

' % now.strftime('%H:%M:%S')

@app.route('/stream')
def stream():
    return Response(get_time(), mimetype='text/event-stream')

if __name__ == '__main__':
    app.run(debug=True)

from flask import Flask, render_template, Response
import requests
import json
import time

app = Flask(__name__)

# Specify the stock to get the data for
symbol = 'AAPL'

# Define a function to get the stock price data
def get_stock_data(symbol):
    url = f'https://www.alphavantage.co/query?function=GLOBAL_QUOTE&symbol={symbol}&apikey=demo'
    while True:
        try:
            response = requests.get(url)
            data = json.loads(response.text)['Global Quote']
            symbol = data['01. symbol']
            price = data['05. price']
            change_percent = data['10. change percent']
            print(f'{symbol}: {price} ({change_percent})')
            yield 'data: %s

' % f'{symbol}: {price} ({change_percent})'
        except Exception as e:
            print(e)
        time.sleep(5)

# Create a Flask route to stream the stock price data
@app.route('/stream')
def stream():
    return Response(get_stock_data(symbol), mimetype='text/event-stream')

# Create a Flask route to render the webpage
@app.route('/')
def index():
    return render_template('index.html')

if __name__ == '__main__':
    app.run(debug=True)
